const gutil = require('gulp-util');
const through = require('through2');
const blocks = require('./handler');

/**
 * Gulp plug-in for automaticly creating folders and stylesheet files
 * for bem-blocks from html-code
 * @author Alexander Gruzdkov
 * @module gulp-bem-css
 */
module.exports = (options) => {
  let folder, extension, elementSeparator, modifierSeparator, param;

  for (param in options) {
    switch (param) {
      case 'folder':
        folder = options[param];
        break;
      case 'extension':
        extension = options[param];
        break;
      case 'elementSeparator':
        elementSeparator = options[param];
        break;
      case 'modifierSeparator':
        modifierSeparator = options[param];
        break;
    }
  }

  return through.obj(function (file, enc, cb) {
    if (file.isNull()) {
      cb(null, file);
      return;
    }

    if (file.isStream()) {
      cb(new gutil.PluginError('BEM Styles Error', 'Streaming not supported'));
      return;
    }

    try {
      const data = file.contents.toString();

      blocks(data, folder, extension, elementSeparator, modifierSeparator);

      file.contents = Buffer.from(data, 'utf8');

      this.push(file);
    } catch (err) {
      this.emit('error', new gutil.PluginError('BEM Styles Error', err));
    }
    this.push(file);
    cb();
  });
};
